import React, { useState ,useEffect} from 'react'
import './banner.css';
import { IoIosArrowDroprightCircle, IoIosArrowDropleftCircle } from 'react-icons/io';


const images = [
    {
        src: 'https://halalwatchworld.news/wp-content/uploads/2020/12/jmia-1600.jpg',
        link: ''
    }, {
        src: 'data:image/jpeg;base64,',
        link: ''
    },
    {
        src: 'https://blog.jumia.com.ng/wp-content/uploads/2015/08/Money-banner.jpg',
        link: ''
    },
    {
        src: 'https://tekedia-website.s3.us-east-2.amazonaws.com/wp-content/uploads/2017/06/06173644/jumia-800x450.jpg',
        link: ''
    },
    {
        src: 'https://reportcircle.com/wp-content/uploads/2017/09/hot-deal-on-Jumia-Nigeria.jpg',
        link: ''
    },
    {
        src: 'https://www.bellanaija.com/wp-content/uploads/2018/07/Finale-creative-blg-600x221.jpg',
        link: ''
    },
    {
        src: 'data:image/png;base64,',
        link: ''
    }
]

export const Banner = () => {
    const [current, setCurrent] = useState(0);
    const [Ishow, setshow] = useState('');
const left=()=>{
    setCurrent(current === 0 ? images.length - 1 : current - 1)
}
const right=()=>{
    setCurrent(current === images.length - 1 ? 0 : current + 1)
}

useEffect(()=>{
    let next=setInterval(()=>{
        right();
    }
    ,3000);
return ()=>clearInterval(next);
},[current])

    const show = images.filter((e, i) => current === i);
    return (
        <div className={`banner ${Ishow}`}  onMouseOver={()=>setshow('Ishow')} onMouseOut={()=>setshow('')} >

            <span id='left' className={current===0&&'none'} onClick={left}><IoIosArrowDropleftCircle /></span>
            <a href={show[0].link} ><img src={show[0].src} /></a>
            <span id='right' onClick={right} className={current===images.length - 1&&'none'}><IoIosArrowDroprightCircle /></span>
        </div>
    )
}
